import React, { useState } from 'react'
import { useSpring } from 'react-spring'

export default function ({ movieSrc, setShowImg }) {
  const [fullMenuVisible, setFullMenuVisible] = useState(false)
  const secondStyle = {
    objectFit: 'cover',
    width: '100%',
    height: '250px',
  }
  console.log(fullMenuVisible)

  const fullImgVisible = useSpring({
    transform: fullMenuVisible ? `translateY(0)` : `translateY(-100%)`,
    opacity: fullMenuVisible ? 1 : 0,
  })

  return (
    <img
      onClick={() => setFullMenuVisible(!fullMenuVisible)}
      style={fullImgVisible}
      src={movieSrc}
      alt=""
    />
  )
}
