import React from 'react'
// import { useSpring } from 'react-spring'

const AnimatedMovieCover = ({ movieSrc, setShowImg }) => {
  const fullImgVisible = useSpring({
    transform: fullImgVisible ? `translateY(0)` : `translateY(-100%)`,
    opacity: fullImgVisible ? 1 : 0,
  })

  return <img style={fullImgVisible} src={movieSrc} alt="" />
}

export default AnimatedMovieCover
