import React, { useState, useEffect } from 'react'
import Hero from './common/hero'
import { getMoviesApi } from '../services/movieService'

const FavoriteMovie = ({ props }) => {
  const [movies, setMovies] = useState([])
  const hero =
    'https://images.pexels.com/photos/3692639/pexels-photo-3692639.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260'

  useEffect(() => {
    async function fetchMovies() {
      const { data: movies } = await getMoviesApi()
      setMovies({ movies })
    }

    fetchMovies()

    console.log(movies)
  }, [])

  return (
    <div className="container">
      <div className="bg-image mt-2 mb-5">
        <Hero
          bgImg={hero}
          title="Favorite Movies"
          description="list all of your favorite movies"
        />
      </div>

      <div className="row">
        {movies.map((movie) => {
          ;<div className="col-lg-6">{movie.title}</div>
        })}
      </div>
    </div>
  )
}

export default FavoriteMovie
