import React from 'react';
import {Link} from 'react-router-dom';
import defaultProfile from '../../utils/images/person_placeholder.jpg';

const img = {
    width:'100px',
    height:'100px',
    borderRadius:'50%',
    objectFit: 'cover',
    marginRight:'1.5rem'
}


const Customer = ({imageBase64, name, phone, isGold, _id}) => {
    const renderImgProfile = image => {
        
        if (image === null && localStorage.getItem('img') === null ) {
            return defaultProfile
        } else if (image !== null){
            localStorage.setItem('img', imageBase64)
            return require(`../../utils/images/${imageBase64}`).default
        } else if (image === null && localStorage.getItem('img') !== null) {
            const imgFromLocalStorage = localStorage.getItem('img');
            return require(`../../utils/images/${imgFromLocalStorage}`).default
        }
    }
    
   
    return (
        <div style={{display:'flex',marginBottom:'2rem', borderBottom: '1px solid black'}}>
            

            {/* {<img style={img} className="img-fluid" src={process.env.PUBLIC_URL + `images/${imageBase64}`}/> } */}
            <img />
           
            <div>
                <Link to={`/customers/${_id}`}>{name}</Link>
                <p>{phone}</p>
                <p>Is Gold: {isGold ? 'yes' : 'no'}</p>
            </div>
        </div>
    )
}

export default Customer;
