import React from 'react'
import { animated, useSpring } from 'react-spring'

export default function ({ movieSrc, setShowImg, handleHideCover }) {
  const secondStyle = {
    objectFit: 'cover',
    width: '100%',
    height: '250px',
  }

  const fullImgVisible = useSpring({
    transform: setShowImg ? `translateY(0)` : `translateY(-100%)`,
    opacity: setShowImg ? 1 : 0,
  })

  return (
    <animated.img
      style={{ ...secondStyle, ...fullImgVisible }}
      src={movieSrc}
      alt=""
    ></animated.img>
  )
}
