import React from "react";
import Joi from "joi-browser";
import Form from "../common/form";

import { getMoviesApi } from "../../services/movieService";
import { getCustomers } from "../../services/customerService";
import { saveReturn } from "../../services/returnService";
import { getRentals } from "../../services/rentalService";
import ReturnedMovies from "./returnedMovies";

class ReturnsForm extends Form {
  constructor(props) {
    super(props);

    this.state = {
      rentals: [],
      movies: [],
      customers: [],
      paymentDetails: {
        customer: "",
        movie: "",
        dateReturned: "",
        rentalFee: 0,
      },
      data: {
        customerId: "",
        movieId: "",
      },
      errors: {},
    };
  }

  schema = {
    customerId: Joi.string().required(),
    movieId: Joi.string().required(),
  };

  async populateRentals() {
    const { data: rentals } = await getRentals();

    this.setState({
      rentals,
    });
  }

  filterArr = () => {
      
  }

  async populateMovies() {
    const { data: movies } = await getMoviesApi();

    this.setState({
      movies,
    });
  }

  async populateCustomers() {
    const { data: customers } = await getCustomers();
    this.setState({
      customers,
    });
  }

  doSubmitReturn = async (e) => {
    const { data } = await saveReturn(this.state.data);
    console.log(data);
    this.setState({
      paymentDetails: {
        customer: data.customer.name,
        movie: data.movie.title,
        dateReturned: data.dateReturned,
        rentalFee: data.rentalFee,
      },
    });
    // this.props.history.push("/rentals");
  };

  componentDidMount() {
    this.populateCustomers();
    this.populateMovies();
    this.populateRentals();
  }

  render() {
    const { rentals } = this.state;
    return (
      <div className="container">
        <div className="row justify-content-between">
          <div className="col-lg-5">
            <h2>Add New Return</h2>

            <form id="return-form" onSubmit={this.handleSubmit}>
              {this.renderSelect("customerId", "Customers", this.state.customers)}
              {this.renderSelect("movieId", "Movies", this.state.movies)}
              {this.renderButton("Save")}
            </form>

            <p style={{ marginTop: "3rem" }}>
              {this.state.paymentDetails.dateReturned &&
                `Customer ${this.state.paymentDetails.customer} has returned the ${this.state.paymentDetails.movie} movie on 
              ${this.state.paymentDetails.dateReturned} with a total fee of ${this.state.paymentDetails.rentalFee}$`}
            </p>
          </div>

          <div className="col-lg-6">
            <ReturnedMovies rentals={rentals} />
          </div>
        </div>
      </div>
    );
  }
}

export default ReturnsForm;
