import React from 'react';
import {Link} from 'react-router-dom';
import defaultProfile from '../../utils/images/person_placeholder.jpg';

const img = {
    width:'100px',
    height:'100px',
    borderRadius:'50%',
    objectFit: 'cover',
    marginRight:'1.5rem'
}


const Customer = ({imageBase64, name, phone, isGold, _id}) => {
    // const renderUserImage = image => {
    //     let img = image === null ? <img style={img} className="img-fluid" src={defaultProfile}/> :  <img style={img} className="img-fluid" src={process.env.PUBLIC_URL + `/images/${imageBase64}`}/>}
    //     return img
    // }
   
    return (
        <div style={{display:'flex',marginBottom:'2rem', borderBottom: '1px solid black'}}>

            {/* {!imageBase64 && <img style={img} className="img-fluid" src={defaultProfile}/>}
            {imageBase64 && <img style={img} className="img-fluid" src={process.env.PUBLIC_URL + `/images/${imageBase64}`}/>}  */}
            {/* {renderUserImage(imageBase64)} */}
            <div>
                <Link to={`/customers/${_id}`}>{name}</Link>
                <p>{phone}</p>
                <p>Is Gold: {isGold ? 'yes' : 'no'}</p>
            </div>
        </div>
    )
}

export default Customer;
