import React from "react";
import Joi from "joi-browser";
import Form from "../common/form";

import { getMoviesApi } from "../../services/movieService";
import { getCustomers } from "../../services/customerService";
import axios from "axios";

class RentalsForm extends Form {
  constructor(props) {
    super(props);

    this.state = {
      data: {
        movies: [],
        customers: [],
      },
      errors: {},
    };
  }

  schema = {
    customerId: Joi.string().required(),
    movieId: Joi.string().required(),
  };

  async populateMovies() {
    const { data: movies } = await getGenresApi();
    this.setState({ movies });
  }

  async populateCustomers() {
    const { data: customers } = await getGenresApi();
    this.setState({ customers });
  }

  componentDidMount() {
    this.populateCustomers();
    this.populateMovies();
  }

  render() {
    return (
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <h1>Add New Rental</h1>

            <form action="">
              {/* {this.renderSelect("isGold", "Gold Customer")}
              {this.renderButton("Save")} */}
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default RentalsForm;
