import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import MoviesTable from './moviesTable'
import ListGroup from './common/listGroup'
import Pagination from './common/pagination'
import {
  getMoviesApi,
  deleteMovieApi,
  editMovie,
} from '../services/movieService'
import { getGenresApi } from '../services/genreService'
import { paginate } from '../utils/paginate'
import _ from 'lodash'
import SearchBox from './searchBox'

import AnimateMovieCover from './animatedMovieCover'
import Hero from './common/hero'

// TODO:: refactor the movieCover to take an object property
// TODO:: changed the movieCover functionality -> onMouseHover remove the component
class Movies extends Component {
  state = {
    movies: [],
    genres: [],
    currentPage: 1,
    pageSize: 4,
    searchQuery: '',
    selectedGenre: null,
    sortColumn: { path: 'title', order: 'asc' },
    currentMovieCover: null,
    fullImgVisible: false,
  }

  async componentDidMount() {
    const { data: movies } = await getMoviesApi()
    const { data: genresApi } = await getGenresApi()

    const genres = [{ _id: '', name: 'All Genres' }, ...genresApi]
    this.setState({
      movies,
      genres,
    })
  }

  //!:: optimistic delete
  handleDelete = async (movie) => {
    const originalMovies = this.state.movies

    const movies = originalMovies.filter((m) => m._id !== movie._id)
    this.setState({ movies })

    try {
      await deleteMovieApi(movie._id)
    } catch (err) {
      if (err.response && err.response.status === 404) {
        console.log(`Error: ${err}`)

        this.setState({
          movies: originalMovies,
        })
      }
    }
  }

  handleLike = async (movie) => {
    const movies = [...this.state.movies]
    const index = movies.indexOf(movie)
    movies[index] = { ...movies[index] }
    movies[index].liked = !movies[index].liked
    movies[index].genreId = movies[index].genre._id

    await editMovie(movies[index]._id, movies[index])

    this.setState({ movies })
  }

  handlePageChange = (page) => {
    this.setState({ currentPage: page })
  }

  handleGenreSelect = (genre) => {
    this.setState({ selectedGenre: genre, searchQuery: '', currentPage: 1 })
  }

  handleSearch = (query) => {
    this.setState({ searchQuery: query, selectedGenre: null, currentPage: 1 })
  }

  handleSort = (sortColumn) => {
    this.setState({ sortColumn })
  }

  handleDisplayCover = (id) => {
    const currentMovieCover = this.state.movies.find(
      (movie) => movie._id === id,
    )

    this.setState(
      {
        currentMovieCover,
        fullImgVisible: true,
      },
      () => {
        // console.log(this.state.fullImgVisible)
      },
    )

    //! --> this will not show the updated state (true) because state is asyncronyous. Either call fullImgVisible in render or
    //! --> call the callback for the setState and console.log(fullImgVisible)
    // console.log(this.state.fullImgVisible)
  }

  handleHideCover = () => {
    this.setState({
      fullImgVisible: false,
    })
  }

  getPagedData = () => {
    const {
      pageSize,
      currentPage,
      sortColumn,
      selectedGenre,
      searchQuery,
      movies: allMovies,
    } = this.state

    let filtered = allMovies

    if (searchQuery)
      filtered = allMovies.filter((m) =>
        m.title.toLowerCase().startsWith(searchQuery.toLowerCase()),
      )
    else if (selectedGenre && selectedGenre._id)
      filtered = allMovies.filter((m) => m.genre._id === selectedGenre._id)

    const sorted = _.orderBy(filtered, [sortColumn.path], [sortColumn.order])

    const movies = paginate(sorted, currentPage, pageSize)

    return { totalCount: filtered.length, data: movies }
  }

  render() {
    const { length: count } = this.state.movies
    const {
      pageSize,
      currentPage,
      sortColumn,
      searchQuery,
      currentMovieCover,
    } = this.state

    if (count === 0) return <p>There are no movies in the database.</p>

    const { totalCount, data: movies } = this.getPagedData()

    return (
      <>
        <div className="container">
          <div className="bg-image mt-2 mb-5">
            <Hero
              description="learning how things work in React JS"
              title="movie app"
            />
          </div>

          <div className="row">
            <div className="col-3">
              <ListGroup
                items={this.state.genres}
                selectedItem={this.state.selectedGenre}
                onItemSelect={this.handleGenreSelect}
              />

              {currentMovieCover && (
                <AnimateMovieCover
                  movieSrc={currentMovieCover.movieCover}
                  setShowImg={this.state.fullImgVisible}
                />
              )}
            </div>

            <div className="col">
              <Link
                to="/movies/new"
                className="btn btn-primary"
                style={{ marginBottom: 20 }}
              >
                New Movie
              </Link>

              <p>Showing {totalCount} movies in the database.</p>

              <SearchBox value={searchQuery} onChange={this.handleSearch} />

              <MoviesTable
                movies={movies}
                sortColumn={sortColumn}
                onLike={this.handleLike}
                onDelete={this.handleDelete}
                onSort={this.handleSort}
                onHandleDisplayMovieCover={this.handleDisplayCover}
                handleHideCover={this.handleHideCover}
              />

              <Pagination
                itemsCount={totalCount}
                pageSize={pageSize}
                currentPage={currentPage}
                onPageChange={this.handlePageChange}
              />
            </div>
          </div>
        </div>
      </>
    )
  }
}

export default Movies
