import React from 'react';

const img = {
    width:'100px',
    height:'100px',
    borderRadius:'50%',
    objectFit: 'cover',
    marginRight:'1.5rem'
}


const Customer = ({customer: {}}) => {
    return (
        <div style={{display:'flex',marginBottom:'2rem', borderBottom: '1px solid black'}}>
            <img style={img} className="img-fluid" src={require(`../../utils/images/${commonProps.imageBase64}`).default}/>
            <div>
                <h3>{commonProps.name}</h3>
                <p>{commonProps.phone}</p>
                <p>Is Gold: {commonProps.isGold ? 'yes' : 'no'}</p>
            </div>
        </div>
    )
}

export default Customer;
