import React from "react";
import Joi from "joi-browser";
import Form from "../common/form";

import { getMoviesApi } from "../../services/movieService";
import { getCustomers } from "../../services/customerService";
import { saveReturn } from "../../services/returnService";

class ReturnsForm extends Form {
  constructor(props) {
    super(props);

    this.state = {
      movies: [],
      customers: [],
      paymentDetails: {
        customer: "",
        movie: "",
        dateReturned: "",
        rentalFee: 0,
      },
      data: {
        customerId: "",
        movieId: "",
      },
      errors: {},
    };
  }

  schema = {
    customerId: Joi.string().required(),
    movieId: Joi.string().required(),
  };

  async populateMovies() {
    const { data: movies } = await getMoviesApi();

    this.setState({
      movies,
    });
  }

  async populateCustomers() {
    const { data: customers } = await getCustomers();
    this.setState({
      customers,
    });
  }

  doSubmitReturn = async (e) => {
    const { data } = await saveReturn(this.state.data);
    console.log(data);
    this.setState({
      paymentDetails: {
        customer: data.customer.name,
        movie: data.movie.title,
        dateReturned: data.dateReturned,
        rentalFee: data.rentalFee,
      },
    });
    // this.props.history.push("/rentals");
  };

  componentDidMount() {
    this.populateCustomers();
    this.populateMovies();
  }

  render() {
    return (
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <h1>Add New Return</h1>

            <form id="return-form" onSubmit={this.handleSubmit}>
              {this.renderSelect("customerId", "Customers", this.state.customers)}
              {this.renderSelect("movieId", "Movies", this.state.movies)}
              {this.renderButton("Save")}
            </form>

            <p>Customer {this.state.paymentDetails.customer} has returned </p>
          </div>
        </div>
      </div>
    );
  }
}

export default ReturnsForm;
