import React from 'react'
import { useSpring } from 'react-spring'

export default function ({ movieSrc, setShowImg }) {
  const secondStyle = {
    objectFit: 'cover',
    width: '100%',
    height: '250px',
  }

  console.log(setShowImg)

  const fullImgVisible = useSpring({
    transform: setShowImg ? `translateY(0)` : `translateY(-100%)`,
    opacity: setShowImg ? 1 : 0,
  })

  return (
    <img style={{ ...secondStyle, ...fullImgVisible }} src={movieSrc} alt="" />
  )
}
