import React from 'react'
import { Spring } from 'react-spring/renderprops'

const AnimatedMovieCover = ({ movieSrc, setShowImg }) => {
  return (
    <Spring from={{ value: 0 }} to={{ value: 100 }}>
      {(props) => <img style={props.value} src={movieSrc} alt="" />}
    </Spring>
  )
}

export default AnimatedMovieCover
