  //     width: '100%',
  //     height: '250px',
  //   }

  const fullImgVisible = useSpring({
    transform: setShowImg ? `translateY(0)` : `translateY(-100%)`,
    opacity: setShowImg ? 1 : 0,
  })

  return (
    <img
      onClick={() => setFullMenuVisible(!fullMenuVisible)}
      style={{ ...secondStyle, ...fullImgVisible }}
      src={movieSrc}
      alt=""
    />
  )
}
