import axios from 'axios'
import api from '../config.json'

export async function saveCustomer(movie) {
  if (movie._id) {
    const body = { ...movie }
    delete body._id
    return axios.put(api.CUSTOMERS_API + '/' + movie._id, body)
  }

  return axios.post(api.CUSTOMERS_API, movie)
}
