import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import {getCustomers} from '../../services/customerService'
import Customer from './singleCustomer';


const Customers = () => {

    const [customers, setCustomers] = useState([]);

    useEffect(() =>{ 
        async function fetchCustomers() {
            const {data:customers} = await getCustomers();
            setCustomers(customers);
           
        }

        fetchCustomers()
    }, [])

    

    return (
        <div className="container">
            <div className="row">
                <div className="col-lg-6">
                    <h1>Customers</h1>
                    <Link to="/customers/new">Add New Customer</Link>

                    
                    <div className="renderCustomers">
                        {customers.map(customer => {
                            return <Customer  name={customer.name} phone={customer.phone} imageBase64={customer.imageBase64} isGold={customer.isGold}/>
                        })}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Customers
