import React from 'react'
import { useSpring } from 'react-spring/asd'

export default function ({ movieSrc, setShowImg }) {
  const fullImgVisible = useSpring({
    transform: setShowImg ? `translateY(0)` : `translateY(-100%)`,
    opacity: setShowImg ? 1 : 0,
    width: '100%',
  })

  return <img style={fullImgVisible} src={movieSrc} alt="" />
}
